data.workflow_syntax:
package "ecommerce_simple" {
    use = "@jelou-marketplace/package-conversational-eco"
    inputs = {
        context = {
            type = "STRING"
            value = "Store context"
        }
        personality = {
            type = "STRING" 
            value = "Agent personality"
        }
        firstMessage = {
            type = "STRING"
            value = "{{$message.text}}"
        }
        nativeCatalog = {
            type = "BOOLEAN"
            value = "false"
        }
        catalogValidate = {
            type = "BOOLEAN"
            value = "false"
        }
    }
    variable = "ecommerce_result"
    next = "handle_result"
    next_failed = "handle_error"
}

package "payment_setup" {
    use = "@jelou-marketplace/payment-in-chat-package:1.1.0"
    inputs = {
        environment = {
            type = "STRING"
            value = "DEV"
        },
        provider = {
            type = "STRING"
            value = "POCKET"
        },
        typePayment = {
            type = "STRING"
            value = "PAYMENT"
        },
        currency = {
            type = "STRING"
            value = "USD"
        },
        taxableAmount = {
            type = "NUMBER"
            value = "100.50"
        },
        iva = {
            type = "NUMBER"
            value = "15"
        }
    }
}
