package "ecommerce_simple" {
    use = "@jelou-marketplace/package-conversational-eco"
    inputs = {
        context = {
            type = "STRING"
            value = "Tienda de electrónicos especializada en smartphones"
        }
        personality = {
            type = "STRING"
            value = "Eres un asistente amigable y conocedor de tecnología"
        }
        firstMessage = {
            type = "STRING"
            value = "{{$message.text}}"
        }
        nativeCatalog = {
            type = "BOOLEAN"
            value = "false"
        }
        catalogValidate = {
            type = "BOOLEAN"
            value = "false"
        }
    }
    variable = "ecommerce_result"
    next = "handle_result"
    next_failed = "handle_error"
}

